mapper.select:
select u.user_id,u.username,u.loginname,u.password,r.role_id,r.role_name ,u.last_login,u.report_rights,u.dwpas_rights,u.dwmis_rights
from tb_user u 
left join tb_role r on u.role_id=r.role_id 
where u.status=0 

	and u.loginname like "%"#{loginname}"%" 


	and u.role_id=#{roleId} 


and u.last_login>=#{lastLoginStart} 


and u.last_login<=#{lastLoginEnd}

select * from tb_user where 1=1

and loginname = #{loginname} and password=#{password}


and user_id = #{userId}

select count(user_id) from tb_user where status=0 

 and loginname like "%"#{loginname}"%" 


and role_id=#{roleId} 


and last_login>=#{lastLoginStart} 


and last_login<=#{lastLoginEnd}

mapper.update:
update tb_user set 
loginname=#{loginname},
username=#{username},
role_id=#{roleId} 
,password=#{password} 
 where user_id=#{userId}
